# Calculate Cost Performance Index (CPI)
if doc.custom_earned_value and doc.custom_actual_cost:
    if doc.custom_actual_cost != 0:
        doc.custom_cost_performance_index = round(doc.custom_earned_value / doc.custom_actual_cost, 2)
    else:
        doc.custom_cost_performance_index = 0
else:
    doc.custom_cost_performance_index = 0

# Determine status
if doc.custom_cost_performance_index > 1:
    doc.custom_cpi_status = "Good"
elif doc.custom_cost_performance_index == 1:
    doc.custom_cpi_status = "On Budget"
elif doc.custom_cost_performance_index < 1:
    doc.custom_cpi_status = "Over Budget"
else:
    doc.custom_cpi_status = ""
